mapper.select:
SELECT

FROM im_session_case sc
WHERE sc.session_id = #{sessionId}

SELECT

, se.group_id AS groupId
FROM im_session se INNER JOIN im_session_case sc ON sc.session_id = se.id
WHERE se.user_id = #{userId} AND se.user_type = #{userType}
AND se.available = #{available} AND sc.case_id = #{caseId}
