mapper.select:
SELECT

FROM customer_authority ca
WHERE ca.id = #{authId}

mapper.update:
UPDATE customer_authority ca SET ca.audit_status = #{status},
ca.reject_reason = #{rejectReason},
ca.update_user_id = #{updateUserId}, ca.update_date = NOW() WHERE ca.id = #{posAuthId}

mapper.select:
SELECT upa.id AS id, upa.user_id AS userId, upa.`get` AS `get`, upa.get_rate AS getRate, upa.twitter_status AS twitterStatus, upa.spread AS spread, upa.develop AS develop, upa.create_date AS createTime, upa.audit_status AS auditStatus, upa.pos_card_id AS cardId, upc.card_no AS bankCardNO, upc.name AS bankCardName, upc.id_card_no AS bankCardIdCardNO, upc.mobile_phone AS mobilePhone, upc.bank AS bankName, upc.bank_code AS bankCode, upc.card_type AS cardType, upci.parent_user_id AS parentUserId, upci.channel_user_id AS userId, upci.channel_phone AS channelPhone, upci.channel_remark AS remark, upci.create_date AS relationTime, upci.total_money AS totalWithdrawDeposit, upci.apply_money AS currentWithdrawDeposit FROM user_pos_auth AS upa LEFT JOIN user_pos_channel_info AS upci ON upci.channel_user_id = upa.user_id LEFT JOIN user_pos_card AS upc ON upc.id = upa.pos_card_id
ORDER BY ${orderHelper.fieldName} ${orderHelper.ordination}
LIMIT #{limitHelper.offset}, #{limitHelper.pageSize}
