mapper.select:
SELECT
customerBrokerage + childTwitterBrokerage
FROM (
    SELECT
    SUM(IF(twitter_user_id = #{twitterUserId}, twitter_brokerage, 0)) AS customerBrokerage,
    SUM(IF(parent_user_id = #{twitterUserId}, parent_brokerage, 0)) AS childTwitterBrokerage
    FROM brokerage
    WHERE create_time >= #{beginTime}
    AND create_time <= #{endTime}
) T

mapper.update:
UPDATE brokerage
SET twitter_brokerage_status = #{toStatus},
twitter_brokerage_status_time = NOW()
WHERE twitter_user_id = #{userId}
AND twitter_brokerage_status = #{fromStatus}

    AND create_time <= #{deadline}

UPDATE brokerage
SET parent_brokerage_status = #{toStatus},
parent_brokerage_status_time = NOW()
WHERE parent_user_id = #{userId}
AND parent_brokerage_status = #{fromStatus}

    AND create_time <= #{deadline}

mapper.select:
SELECT uptr.user_id AS userId, SUM(uptb.agent_charge) AS brokerage FROM user_pos_transaction_record uptr LEFT JOIN user_pos_twitter_brokerage uptb ON uptb.record_id = uptr.id WHERE uptr.user_id IN
#{item}
AND (uptr.status = 3 OR uptr.status = 4) GROUP BY uptr.user_id

SELECT upji.channel_user_id AS userId, SUM(uptb.parent_agent_charge) AS brokerage FROM user_pos_junior_info upji LEFT JOIN user_pos_transaction_record uptr ON uptr.user_id = upji.junior_user_id LEFT JOIN user_pos_twitter_brokerage uptb ON uptb.record_id = uptr.id WHERE upji.channel_user_id IN
#{item}
AND (uptr.status = 3 OR uptr.status = 4) AND upji.relation_available = 1 GROUP BY upji.channel_user_id
