mapper.update:
UPDATE user_pos_transaction_record
SET get_agent = #{toAgentStatus}
WHERE agent_id = #{userId}
AND get_agent = #{fromAgentStatus}
AND complete_date IS NOT NULL

    AND complete_date < #{deadline}

mapper.select:
SELECT

FROM user_pos_transaction_record uptr


    ORDER BY ${orderHelper.fieldName} ${orderHelper.ordination}

LIMIT #{limitHelper.offset}, #{limitHelper.pageSize}

SELECT uptr.user_id AS userId, COUNT(*) AS transactionCount, SUM(uptr.amount) AS transactionAmount FROM user_pos_transaction_record uptr WHERE uptr.user_id IN
#{item}
AND (uptr.status = 3 OR uptr.status = 4) GROUP BY uptr.user_id

mapper.update:
UPDATE user_pos_transaction_record
SET out_card_info = #{record.outCardInfo}
WHERE id = #{record.id}
