mapper.update:
UPDATE employee_collection co
SET co.available = #{collection.available},
co.update_time = #{collection.updateTime}
WHERE co.id = #{collection.id}

mapper.select:
SELECT COUNT(*)
FROM employee_collection r
WHERE r.user_id = #{userId}
AND r.user_type = #{userType}
AND r.available = 1

